import { ReactNode, createContext, useContext, useEffect, useRef, useState } from "react";
import { checkBoard, createBoard } from "../functions/function";
import { Contexto, matriz, tablero } from "../models/types/type";
import { Casilla } from "../models/classes/casilla";
import { ModalRef } from "./Modal";




export interface RefContextProps {
    modalRef: React.RefObject<ModalRef>

}

export const ContextoBoard = createContext<Contexto>({
    data: [],
    changeValue: () => { },
    changeColor: () => { },
    RefForModal: undefined
});











type boardContextProps = {
    contenido: matriz,
    children: ReactNode
}
export const BoardContext = (props: boardContextProps) => {

    const tablero = createBoard(props.contenido)
    const [data, setData] = useState(tablero);

    const myRef = useRef<ModalRef>(null)



    function changeValue(number: Casilla, newValue: number | null, data: tablero) {
        const newData = data.map(row => row.slice());
        newData[number.x][number.y].value = newValue;
        setData(newData);

    }


    function changeColor(id: string[], color: string, data: tablero) {

        const newData = data.map(row => row.slice());
        for (let i = 0; i < newData.length; i++) {
            for (let k = 0; k < newData[i].length; k++) {
                for (let x = id.length - 1; x >= 0; x--) {
                    if (newData[i][k].id === id[x]) {
                        newData[i][k].color = color;
                    }
                }
            }
        }
        setData(newData);

    }
    const [winner, setWinner] = useState(false)
    useEffect(() => {
        function checkWin(data: tablero) {
            let winner = true
            data.forEach(rows => {
                rows.forEach(casilla => {
                    if (casilla.value === null || isNaN(casilla.value) || casilla.value === 0) {
                        winner = false
                    } else {
                        if (checkBoard(data)) {
                            winner = false
                        }
                    }
                });
            });
            console.log("estado winnner: ", winner)
            setWinner(winner)
        }
        checkWin(data);
    }, [data]);

    useEffect(() => {
        if (winner) {
            myRef.current?.showModal();
        }
    }, [winner])

    const contexto: Contexto = {
        data: data,
        changeValue: changeValue,
        changeColor: changeColor,
        RefForModal: { modalRef: myRef }
    };

    return (
        <>
            <ContextoBoard.Provider value={contexto}>


                {props.children}


            </ContextoBoard.Provider>

        </>


    )

}


export const useModal = () => {
    const context = useContext(ContextoBoard);
    if (!context.RefForModal) {
        throw new Error('useModal must be used within a ModalProvider');
    }
    return context.RefForModal;
};

import './style.css'

import { Casilla } from '../models/classes/casilla';


type Props = {
    casilla: Casilla;
    onNumberChange: (event:any,number: Casilla) =>void
}





const Box: React.FC<Props> = ({ casilla,onNumberChange }) => {


    return (

        <input type="text"
            maxLength={1}
            onChange={(e)=>{onNumberChange(e,casilla)}}
            value={casilla.value || ""}
            disabled={casilla.defaultValue ? true : false}
            className={casilla.color} 
            data-testid={casilla.id}/>

    )
}


export default Box

import { Col, Container, Row } from "react-bootstrap"
import Box from "./box"
import './style.css'

import {   ContextoBoard } from "./boardContext"
import { checkBoard } from "../functions/function";
import {  useContext, useEffect, useState } from "react";
import { tablero } from "../models/types/type";
import { Casilla } from "../models/classes/casilla";

export const Square = () => {
  const { data, changeValue, changeColor } = useContext(ContextoBoard)
  const content: tablero = data
  const changeValueContext = changeValue
  const changeColorContext = changeColor
  const white = "white";
  const red = "rojo"
  const [id, setId] = useState<string[]>([])

  function handleChange(event: any, casilla: Casilla) {
    const newValue = event.target.value;

    changeValueContext(casilla, isNaN(parseInt(newValue)) ? null : parseInt(newValue),content)

    const boardCheckResult = checkBoard(content);

    if (boardCheckResult) {

     changeColorContext(boardCheckResult, red,content);

    } else {
      changeColorContext(id, white,content)
    }

  }

  useEffect(() => {
    const boardCheckResult = checkBoard(content)
    const previousIds = [...id];

    setId(boardCheckResult ? boardCheckResult : []);

    previousIds.forEach((prevId) => {
      if (!boardCheckResult || !boardCheckResult.includes(prevId)) {
        changeColorContext([prevId], white,content);
      }
    })
  }, [content])


  return (
    <Container className="sudoku-board">
      {content.map((rows, rowIndex) => (

        <Row key={rowIndex} className={`p-0 ${((rowIndex + 1) % 3 === 0 && (rowIndex + 1) % 9 !== 0 ? 'borde' : '')}`} >

          {rows.map((casilla, colIndex) => (
            <Col key={colIndex} className={`p-0 ${((colIndex + 1) % 3 === 0 && (colIndex + 1) % 9 !== 0 ? 'borderight' : '')}`} >
              <Box casilla={casilla} onNumberChange={handleChange} />
            </Col>
          ))}
        </Row>
      ))}
    </Container>
  );
};











export default Square

import './Modal.css'
import 'bootstrap/dist/css/bootstrap.min.css';

import { forwardRef,  useImperativeHandle, useState } from "react";

export type ModalRef = {
  showModal(): void,
  hideModal(): void
}

export const Modal = forwardRef((props,ref) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);

    const handleShow = () => setShow(true);

   
    console.log(props)
    useImperativeHandle(ref, () => ({
        showModal() {
          handleShow();
        },
        hideModal() {
          handleClose();
        },
      }))
    return show ?
        <div>
              <div className="modal-overlay">
      <div className="modal-base d-flex flex-column bg-white">
        <div className='modal-header text-white bg-secondary p-3'>
          <span>Felicitaciones</span>
        </div>
        <div className='modal-body p-3'>
        
          <div>
            Has ganado!
          </div>
        
        </div>
        <div className="modal-actions d-flex justify-content-end p-3 pt-0">
          <button className="btn btn-primary text-white" onClick={() => {  handleClose(); }}>
            Close
          </button>
        </div>
      </div>
    </div>
            
        </div>: null

})
